import React, { Component } from 'react';
import './Hero.css';
import Button from './Button';
import sunandpuddleslogo from '../img/sunandpuddleslogo.png';

class Hero extends Component {
  render() {
    return (
      <section className='heroSection'>
        <div className='heroContent'>
          <h1 className='heroContentTitle'>
            National and local <span className='magicalLine'>forecasts</span><br />in the palm of yourhand.
          </h1>
          <p className='heroContentCopy'>
            Weather can be unpredictable. Keep up-to-date with the latest forecasts<br />with Sun and Puddles for your computer and mobile device.
          </p>
          <Button buttonLabel='Download Today' />
          <img className='heroImage' src={sunandpuddleslogo} alt="Sun and Puddles" />
        </div>
      </section>
    );
  }
}

export default Hero;
